//❗️书籍变量优先级＞源变量
//❗️书籍变量设置为-1，则加载全部目录
//❗️源变量最好设置10以内
var v = source.getVariable();
"" == v || null == v || undefined == v
  ? ((v = "加载章节页：5"), source.setVariable(v))
  : v
function cmpVariable(){
	 sv=source.getVariable().match(/\d+$/);
	 bv=book.getVariable("custom")
	 sv=(sv==""||sv==null)?"":parseInt(sv);
	 bv=(bv==""||bv==null)?"":parseInt(bv);
	 nv=0;
	 if(bv==-1){return nv}
	 if(bv!=""){nv=bv}else
	 if(sv!=""){nv=sv}
	 return nv;
}
function getBookList(name,lc,inf,cover,href){
	lis=[];
	ns=(name=="")?"":java.getStringList(name);
	lcs=(lc=="")?"":java.getStringList(lc);
	infs=(inf=="")?"":java.getStringList(inf);
	cs=(cover=="")?"":java.getStringList(cover);
	hs=(href=="")?"":java.getStringList(href);
	//java.log(ns)

	for(var i=0;i<ns.length;i++){
		    jon={"name":ns[i],
		     "href":hs[i],"info":infs[i],
		     "auth":baseUrl.match(/(http[s]*:\/\/[^/]+)/)[1],
		         "cover":cs[i],"lc":lcs[i]};
		java.log(ns[i]+"||"+hs[i]+"||"+cs[i]+"||"+lcs[i]);
		    lis.push(jon);
  }
	return lis;
}
function getChapterList(cn,cu,ci){
	   cs=[];
	   n=(cn=="")?"":java.getStringList(cn);
    u=(cu=="")?"":java.getStringList(cu);
    ii=(ci=="")?"":java.getStringList(ci);

   for(var i=0;i<n.length;i++){
   	   jso={"cn":n[i],"cu":u[i],"ci":ii[i]};

   	 //java.log(JSON.stringify(jso))
   	  cs.push(jso);
   }
   return cs;
	}
//参数说明：
//imgr:图片规则,可为img@src或者@img@html
//wdr:正文文字规则，没有置""
//bsurl:图片src属性不含域名，bsurl填域名
//       否则置""
//isheads:解决图片防盗链，没有填""，否则置
//       {"headers"{"Referer":baseUrl}}
function getContent(imgr,wdr,bsurl,isheads){
	wds=(wdr=="")?"":java.getString(wdr);
	tus=java.getString(imgr);
	var imgs="";
 heads=(isheads=="")?"":","+JSON.stringify(isheads)

	if(/@html/.test(imgr)){
		  	imgs=tus;
		}else{
			imgs=tus.split("\n");
			for(var i=0;i<imgs.length;i++){
				if(imgs[i]!=""){
				  imgs[i]='<img  src="'+
				        	bsurl+imgs[i]+heads+'" />'
				   }
				   java.log(imgs[i])
    }
			 imgs=imgs.join("\n")
		}
		return wds+imgs;
}

